query:
UPDATE tasks t
SET t.sharedHouseholdIds = (
  SELECT JSON_ARRAYAGG(st.sharedWithHouseholdId)
  FROM shared_tasks st
  WHERE st.taskId = t.id
)
WHERE EXISTS (
  SELECT 1 FROM shared_tasks st2 WHERE st2.taskId = t.id
);

